SELECT plan_uid AS planUid, business_uid AS businessUid, conversation_uid AS conversationUid, temp_uid AS copyWritingUid, temp_uid AS pictureTempUid, use_picture AS usePicture, execute_params AS pictureParams, execute_params AS copyWritingParams, status AS pictureStatus, status AS copyWritingStatus, start_time AS pictureStartTime, end_time AS pictureEndTime, execute_time AS pictureExecuteTime, start_time AS copyWritingStartTime, end_time AS copyWritingEndTime, execute_time AS copyWritingExecuteTime, copy_writing_title AS copyWritingTitle, copy_writing_content AS copyWritingContent, picture_num AS pictureNum, picture_content AS pictureContent, error_msg AS pictureErrorMsg, error_msg AS copyWritingErrorMsg, retry_count AS pictureRetryCount, retry_count AS copyWritingRetryCount, copy_writing_count AS copyWritingCount, extend AS pictureExtend, extend AS copyWritingExtend, claim AS claim, liked AS liked, tags AS tags FROM llm_creative_content WHERE type = 'all' AND deleted = false
AND plan_uid = #{req.planUid}
AND scheme_uid = #{req.schemeUid}
AND claim = #{req.claim}
AND status = #{req.status}
AND is_test = #{req.isTest}
ORDER BY id

SELECT a.* FROM llm_creative_content a INNER JOIN llm_creative_plan b on a.plan_uid = b.uid WHERE a.retry_count < 3 and b.status in ('RUNNING','FAILURE') and a.deleted = false and b.deleted = false
and a.plan_uid = #{req.planUid}
and a.type = #{req.type}
and a.retry_count >= 1 and a.status = 'execute_error'
and a.is_test = 1
and a.status != 'execute_success' and a.status != 'executing' ORDER BY a.update_time ASC
LIMIT #{req.bathCount}

SELECT a.* FROM llm_creative_content a WHERE a.retry_count < 3 and a.deleted = false
and a.plan_uid = #{req.planUid}
and a.type = #{req.type}
and a.retry_count >= 1 and a.status = 'execute_error'
and a.is_test = 1
and a.status != 'execute_success' and a.status != 'executing' ORDER BY a.update_time ASC
LIMIT #{req.bathCount}

