mapper.select:
select count(*)
from llm_notification
where deleted = false

    and name = #{reqVO.name}


    and field = #{reqVO.field}


    and status = #{reqVO.status}


    and create_time > DATE_FORMAT(#{reqVO.createStartTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%S')


    and create_time <= DATE_FORMAT(#{reqVO.createEndTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%S')

select n.*, u.username as createUser, COUNT(CASE WHEN m.deleted = false and m.status = 'stay_claim' THEN 1 END) AS stayClaimCount, COUNT(CASE WHEN m.deleted = false and m.status in ('claimed') THEN 1 END) AS claimCount, COUNT(CASE WHEN m.deleted = false and m.status in ('published', 'pre_settlement', 'pre_settlement_error') THEN 1 END) AS publishedCount, COUNT(CASE WHEN m.deleted = false and m.status in ('settlement', 'settlement_error') THEN 1 END) AS settlementCount, COUNT(CASE WHEN m.deleted = false THEN 1 END) AS total from llm_notification n left join llm_single_mission m on m.notification_Uid = n.uid left join system_users u on m.creator = u.id where n.deleted = false
and n.name = #{reqVO.name}
and n.field = #{reqVO.field}
and n.status = #{reqVO.status}
and n.create_time > DATE_FORMAT(#{reqVO.createStartTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%S')
and n.create_time <= DATE_FORMAT(#{reqVO.createEndTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%S')
group by n.uid order by n.create_time desc limit #{start},#{size}

select count(1)
from llm_notification
where deleted = false and end_time > now() and status = 'published'

    and open = #{reqVO.open}


    and platform = #{reqVO.platform}


    and field = #{reqVO.field}


    and creator in

        #{item}

select n.*,
COUNT(case WHEN m.`status` in ('claimed','published','pre_settlement','settlement','pre_settlement_error','settlement_error')  THEN 1 end ) claimCount,
COUNT(case WHEN m.`claim_user_id` = #{reqVO.claimUserId} THEN 1 end ) currentUserNum
from llm_notification n
LEFT JOIN llm_single_mission m ON m.notification_Uid = n.uid
where n.deleted = false and n.end_time > now() and n.status = 'published'

    and n.open = #{reqVO.open}


    and n.platform = #{reqVO.platform}


    and n.field = #{reqVO.field}


    and n.creator in

        #{item}


GROUP BY n.id
order by ${field} ${type}
limit #{start},#{size}
