mapper.select:
select m.id from llm_single_mission m inner join llm_notification n on m.notification_Uid = n.uid where m.deleted = false and n.deleted = false and n.start_time <= CURDATE() and n.end_time >= DATE_SUB(CURDATE(), INTERVAL 1 DAY) and (m.pre_settlement_time is null or m.pre_settlement_time < CURDATE()) and (m.settlement_time is null or m.settlement_time < CURDATE()) and m.status in ('published','pre_settlement') and n.status = 'published'
and n.type = #{reqVO.singleMissionType} and m.type = #{reqVO.singleMissionType}
order by m.id
limit #{reqVO.limitSize}
