mapper.select:
select count(*)
from llm_single_mission
where deleted = false and notification_Uid = #{reqVO.notificationUid}

    and status = #{reqVO.status}


    and claim_user_id = #{reqVO.claimUserId}


    and claim_username = #{reqVO.claimUsername}

select m.*, n.liked_count as likedCount,n.collected_count as collectedCount,n.comment_count as commentCount
from llm_single_mission m
left join llm_xhs_note_detail n on m.note_id = n.note_id
where m.deleted = false and m.notification_Uid = #{reqVO.notificationUid}

    and m.status = #{reqVO.status}


    and m.claim_user_id = #{reqVO.claimUserId}


    and m.claim_username = #{reqVO.claimUsername}

order by m.create_time desc
limit #{start},#{end}
