mapper.select:
select n.id,
n.uid,n.name,n.thumbnail,n.type,n.category_id,n.material_tags,n.overt_status,n.status,n.associated_id,n.creator,n.updater,n.update_time,
n.create_time, count(m.id) as materialCount
from poster_material_group n
left join poster_material m on m.group_id = n.id
where n.deleted = false and n.status = true and m.deleted = false and m.status = true

    and n.name = #{reqVO.name}


    and n.category_id = #{reqVO.categoryId}


    and n.overt_status = #{reqVO.overtStatus}


    and n.type = #{reqVO.type}


    and n.user_type = #{reqVO.userType}

group by n.id,
n.uid,n.name,n.thumbnail,n.type,n.category_id,n.material_tags,n.overt_status,n.status,n.associated_id,n.creator,n.updater,n.update_time,
n.create_time
order by n.create_time desc

select n.id,
n.uid,n.name,n.thumbnail,n.type,n.category_id,n.material_tags,n.overt_status,n.status,n.associated_id,n.creator,n.updater,n.update_time,
n.create_time, count(m.id) as materialCount
from poster_material_group n
left join poster_material m on m.group_id = n.id
where n.deleted = false and n.status = true and m.deleted = false and m.status = true and n.overt_status = true and n.associated_id is not null

    and n.name = #{reqVO.name}


    and n.category_id = #{reqVO.categoryId}


    and n.overt_status = #{reqVO.overtStatus}


    and n.type = #{reqVO.type}


    and n.user_type = #{reqVO.userType}

group by n.id,
n.uid,n.name,n.thumbnail,n.type,n.category_id,n.material_tags,n.overt_status,n.status,n.associated_id,n.creator,n.updater,n.update_time,
n.create_time
order by n.create_time desc

select n.id,
n.uid,n.name,n.thumbnail,n.type,n.category_id,n.material_tags,n.overt_status,n.status,n.associated_id,n.creator,n.updater,n.update_time,
n.create_time, count(m.id) as materialCount
from poster_material_group n
left join poster_material m on m.group_id = n.id
where n.deleted = false and n.status = true and m.deleted = false and m.status = true and n.creator = #{reqVO.createId} and n.overt_status = false and n.associated_id is null

    and n.name = #{reqVO.name}


    and n.category_id = #{reqVO.categoryId}


    and n.overt_status = #{reqVO.overtStatus}


    and n.type = #{reqVO.type}


    and n.user_type = #{reqVO.userType}

group by n.id,
n.uid,n.name,n.thumbnail,n.type,n.category_id,n.material_tags,n.overt_status,n.status,n.associated_id,n.creator,n.updater,n.update_time,
n.create_time
order by n.create_time desc
